# 保存聊天内容
def save_chat_log(request):
    try:
        print("后端收到了ajax消息")
        chatinfo = ChatInfo()

        # 获取前端传过来的数据
        chat_content = wrappers.post(request, "chat_content")
        user_ip = wrappers.get_client_ip(request)
        user_name = wrappers.post(request, "user_name")
        user_id = wrappers.post(request, "user_id")

